CREATE DATABASE IF NOT EXISTS polusi;
USE polusi;

CREATE TABLE negara (
    kode_negara VARCHAR(3) PRIMARY KEY,
    nama_negara VARCHAR(100),
    benua VARCHAR(100),
    created_at TIMESTAMP
);

DESCRIBE negara;


CREATE TABLE kota(
    id_kota INT PRIMARY KEY,
    kode_negara VARCHAR(3),
    nama_kota VARCHAR(200),
    FOREIGN KEY (kode_negara) REFERENCES negara(kode_negara)
);


CREATE TABLE populasi_negara(
    id_populasi_negara INT PRIMARY KEY,
    kode_negara VARCHAR(3),
    tahun INT,
    jumlah_populasi BIGINT,
    FOREIGN KEY (kode_negara) REFERENCES negara(kode_negara)
);


CREATE TABLE populasi_kota(
    id_populasi_kota INT PRIMARY KEY,
    id_kota INT,
    tahun INT,
    jumlah_populasi INT,
    FOREIGN KEY(id_kota) REFERENCES kota(id_kota)
);


CREATE TABLE polusi(
    id_polusi INT PRIMARY KEY,
    id_kota INT,
    tahun INT,
    index_kualitas_udara DECIMAL(6,2),
    index_co2 DECIMAL(6,2),
    index_ozone DECIMAL(6,2),
    index_no2 DECIMAL(6,2),
    pm25 DECIMAL(6,2),
    FOREIGN KEY(id_kota) REFERENCES kota(id_kota)
);


CREATE TABLE kualitas_hidup(
    id_kualitas_hidup INT PRIMARY KEY,
    id_kota INT,
    tahun INT,
    index_kualitas_hidup DECIMAL(5,2),
    index_keamanan DECIMAL(5,2),
    index_kesehatan DECIMAL(5,2),
    index_pendidikan DECIMAL(5,2),
    index_biaya_hidup DECIMAL(5,2),
    FOREIGN KEY(id_kota) REFERENCES kota(id_kota)
);

